mapper.update:

	UPDATE  recruit_tb 
	SET recruit_title=#{recruitTitle},recruit_detail=#{recruitDetail}, work_area_id=#{workAreaId}, recruit_start_at=#{recruitStartAt}, recruit_end_at=#{recruitEndAt}
    WHERE recruit_id=#{recruitId}


UPDATE recruit_tb
SET recruit_end_at=NOW()
WHERE recruit_id=#{recruitId}
